import styled from 'styled-components'

export default function Day(props){

    const {key, index, day, isSelected, setIsSelected, selectingDay, selectedDays} = props;

    function selectDay(){
        selectingDay(day, index);
    }

    return(
        <Button key={key} index={index} day={day} onClick={selectDay}>
            {day}
        </Button>
    )
}



function colorDay(selectedDay){
    if(selectedDay) return "#000"
    else if(!selectedDay) return "#FFFFFF"
    console.log('entrei em colorday')
}

const Button = styled.button`
    height: 100%;
    width: 100%;

    display: flex;
    justify-content: center;
    align-items: center;

    background-color: ${props => colorDay(props.selectedDay)};
    border: 1px solid #D5D5D5;
    border-radius: 5px;
    font-size: 18px;
    color: #D5D5D5;
`;
