import { useState, useContext, useEffect } from "react";
import UserContext from "./contexts/UserContext";
import axios from "axios";
import styled from "styled-components";

export default function CreareHabtis(){

    const {token} = useContext(UserContext);

    const serializedUsedData = localStorage.getItem("localUserData");
    const localUserData = JSON.parse(serializedUsedData);

    const [habits, setHabits] = useState([]);

    const config = {
        headers: {
            Authorization: `Bearer ${localUserData.token}`
        }
    }

    useEffect(()=>{

        const promisse = axios.get('https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits', config);

        promisse.then((response) => {
            setHabits(response.data);
            console.log(response.data);
        });
        promisse.catch(() => console.log("deu ruim..."));

    },[])

    function renderHabits(){
        return(
            <ConfigureHabit key={1}>
                <Days>
                    {habits.days.map((day, index) => <DiaDaSemana index={index} day={day} />)}
                </Days>
            </ConfigureHabit>
        );
    }

    return(
        (habits.length !== null)?
        <p>"Já tem aqui"</p>
        :
        <>
        <Message>
            {"Você não tem nenhum hábito cadastrado ainda. Adicione um hábito para começar a trackear!"}
        </Message>
        </>
    );
}

function DiaDaSemana({toggleDay, day, index}) {
    const [selected, setSelected] = useState(false);

    return (
        <Divao onClick={() => { setSelected(!selected); toggleDay(selected, index) }} selected={selected}>{day}</Divao>
    )
}

const Message = styled.div`
    font-size: 18px;
    color: #666666;
`;

const ConfigureHabit = styled.div`
height: 25vh;
width: 100%;
background-color: #ffffff;

border-radius: 5px;
padding: 15px;
box-sizing: border-box;

margin-bottom: 10px;

position: relative;
`;

const Days = styled.div`
    width: 75%;

    display: flex;
    flex-direction: row;
    justify-content: space-between;
`;
