import { useState, useContext, useEffect } from "react";
import UserContext from "./contexts/UserContext";
import axios from "axios";
import styled from "styled-components";

export default function CreateHabits(){

    const {token} = useContext(UserContext);

    const serializedUsedData = localStorage.getItem("localUserData");
    const localUserData = JSON.parse(serializedUsedData);

    const [habits, setHabits] = useState([]);

    const config = {
        headers: {
            Authorization: `Bearer ${localUserData.token}`
        }
    }

    useEffect(()=>{

        const promisse = axios.get('https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits', config);

        promisse.then((response) => {
            setHabits(response.data);
            console.log(response.data);
        });
        promisse.catch(() => console.log("deu ruim..."));

    },[])

    function renderHabits(){
        return(
            <ConfigureHabit key={1}>
                <Days>
                    {habits.days.map((day, index) => <DiaDaSemana index={index} selectedDay={day} />)}
                </Days>
            </ConfigureHabit>
        );
    }

    return(
        (habits.length !== null)?
            () => (renderHabits)
        :
        <>
        <Message>
            {"Você não tem nenhum hábito cadastrado ainda. Adicione um hábito para começar a trackear!"}
        </Message>
        </>
    );
}

function DiaDaSemana({selectedDay, index}) {
    const [selected, setSelected] = useState(false);
    const days = ["D", "S", "T", "Q", "Q", "S", "S"];

    return (

        days.map((day, index) => {
            return <Divao
                key={index}
                color={(selectedDay === day? "#D5D5D5" : "#FFFFFF")}
                background={(selectedDay === day? "#FFFFFF" : "#D5D5D5")}>
                {day}
            </Divao>
        })
    )
}

const Message = styled.div`
    font-size: 18px;
    color: #666666;
`;

const ConfigureHabit = styled.div`
height: 25vh;
width: 100%;
background-color: #ffffff;

border-radius: 5px;
padding: 15px;
box-sizing: border-box;

margin-bottom: 10px;

position: relative;
`;

const Days = styled.div`
    width: 75%;

    display: flex;
    flex-direction: row;
    justify-content: space-between;
`;
const Divao = styled.div`
    width: 30px;
    height: 30px;
    background-color: ${props => props.background};
    color: ${props => props.color};
`;
