import styled from 'styled-components';
import { useState, useContext } from 'react';
import { Link } from 'react-router-dom';

import BigLogo from "./BigLogo";
import UserContext from './contexts/UserContext';

export default function SingUp(){

    const {userData, setUserData} = useContext(UserContext);

    function sendUserData(){
        setUserData({...userData});
        console.log(userData);
    }
    function postUserData(){
        const request = axios.post('https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/auth/sign-up', userData);
        request.then(console.log(request.data))
    }

    return(
        <>
            <BigLogo/>
            <Container>
                <Form onSubmit={postUserData}>
                    <input type="email" placeholder="email" value={userData.email} onChange={(e) => sendUserData(userData.email = e.target.value)}/>
                    <input type="password" placeholder="senha" value={userData.password} onChange={(e) => sendUserData(userData.password = e.target.value)}/>
                    <input type="text" placeholder="nome" value={userData.name} onChange={(e) => sendUserData(userData.name = e.target.value)}/>
                    <input type="url" placeholder="foto" value={userData.image} onChange={(e) => sendUserData(userData.image = e.target.value)}/>
                    <Button type="submit" >{"Cadastrar"}</Button>
                </Form>
            </Container>
            <Link to="/">
                {"Já tem uma conta? Faça login!"}
            </Link>
        </>
    );
}

const Container = styled.div`
    height: 34vh;
    width: 90%;

    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;

    margin-bottom: 8%;

`;

const Form = styled.form`
    height: 79%;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;

        input{
            height: 20.5%;
            border: 1px solid #D5D5D5;
            border-radius: 5px;
            font-size: 20px;
            color:#DBDBDB;
        }
`;

const Button = styled.button`
    height: 6vh;
    width: 80vw;
    background-color: #52B6FF;
    font-size: 20px;
    color: #FFFFFF;
`;
