import { useContext, useEffect } from 'react';
import axios from 'axios';

import styled from 'styled-components';
import HabitsContext from './contexts/HabitsContext';

export default function Habit() {
    const days = ["D", "S", "T", "Q", "Q", "S", "S"];

    const { habits, setHabits } = useContext(HabitsContext)

    const serializedUsedData = localStorage.getItem("localUserData");
    const localUserData = JSON.parse(serializedUsedData);
    //const selectedDay = habits.days; 
    // /\ voce nao precisa dessa variavel, ela nem sempre será acessível devido à questões de escopo. 
    // Mas o estado (habits) é sempre acessível, então basta chamar habits.days onde precisa

    const config = {
        headers: {
            Authorization: `Bearer ${localUserData.token}`
        }
    }

    useEffect(() => {

        const promisse = axios.get('https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits', config);

        promisse.then((response) => {
            setHabits(response.data);
            console.log(response.data);
        });
        promisse.catch(() => window.alert("deu ruim..."));
        // eslint-disable-next-line 
    }, []);

    return (
        // eslint-disable-next-line 
        habits.map((habit, id) => {
            console.log(habit);

            return (
                <SessionHabit key={id}>
                    <h1>{habit.name}</h1>

                    <Days key={id}>
                        {
                            days.map((day, index) => {
                                // Depois de um map sempre precisa de um return
                                // E RenderDays é uma função e não componente, estava com <RenderDays>
                                return (
                                    renderDays(day, index, habit.days)
                                )
                            })
                        }
                    </Days>
                </SessionHabit>
            )
        })

    )
}

function renderDays(day, index, habitDays) {
    // pra vc entender o que tá acontecendo, olha o console aqui:
    console.log("day: ", day);
    console.log("index: ", index);
    console.log("habitDays: ", habitDays);

    return (
        <Day
            key={index}
            color={(habitDays.includes(index) ? "#D5D5D5" : "#FFFFFF")}
            background={habitDays.includes(index) ? "#FFFFFF" : "#D5D5D5"}>
            {day}
        </Day>
    )
}

const SessionHabit = styled.div`
    height: 20vh;
    width: 100%;
    background-color: #ffffff;

    border-radius: 5px;
    padding: 15px;
    box-sizing: border-box;

    margin-bottom: 10px;
`;

const Days = styled.div`
    width: 75%;

    display: flex;
    flex-direction: row;
    justify-content: space-between;

    background-color: purple;
`;

const Day = styled.div`
    width: 30px;
    height: 30px;
    background-color: ${props => props.background};
    color: ${props => props.color};
`;
