import { useContext, useEffect } from 'react';
import axios from 'axios';

import styled from 'styled-components';
import HabitsContext from './contexts/HabitsContext';

import trash from '../image/trash.png';

export default function Habit() {
    const days = ["D", "S", "T", "Q", "Q", "S", "S"];

    const { habits, setHabits } = useContext(HabitsContext)

    const serializedUsedData = localStorage.getItem("localUserData");
    const localUserData = JSON.parse(serializedUsedData);

    const config = {
        headers: {
            Authorization: `Bearer ${localUserData.token}`
        }
    }

    useEffect(() => {

        const promisse = axios.get('https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits', config);

        promisse.then((response) => {
            setHabits(response.data);

        });
        promisse.catch(() => window.alert("deu ruim..."));
        // eslint-disable-next-line 
    }, []);

    return (
        // eslint-disable-next-line 
        habits.map((habit, id) => {
            console.log(habit);

            return (
                <SessionHabit key={id}>
                    <HabitName>
                        <h1>{habit.name}</h1>
                        <img src={trash} alt="trash"/>
                    </HabitName>


                    <Days key={id}>
                        {
                            days.map((day, index) => {

                                return (
                                    renderDays(day, index, habit.days)
                                )
                            })
                        }
                    </Days>
                </SessionHabit>
            )
        })

    )
}

function renderDays(day, index, habitDays) {

    return (
        <Day
            key={index}
            color={(habitDays.includes(index) ? "#D5D5D5" : "#FFFFFF")}
            background={habitDays.includes(index) ? "#FFFFFF" : "#D5D5D5"}>
            {day}
        </Day>
    )
}

const SessionHabit = styled.div`
    height: 20vh;
    width: 100%;
    background-color: #ffffff;

    border-radius: 5px;
    padding: 15px;
    box-sizing: border-box;

    margin-bottom: 10px;

        img{
            height: 15px;
            width: 15px;
        }
`;
const HabitName = styled.div`
    height: 20%;
    background-color: yellow;

    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
`;

const Days = styled.div`
    width: 75%;

    display: flex;
    flex-direction: row;
    justify-content: space-between;

    background-color: purple;
`;

const Day = styled.div`
    width: 30px;
    height: 30px;
    background-color: ${props => props.background};
    color: ${props => props.color};
`;
