import {useState, useContext, useEffect} from 'react';
import axios from 'axios';
import styled from 'styled-components';

import Header from './Header';
import Footer from './Footer';

import HabitsContext from './contexts/HabitsContext';
import check from '../image/check.png'


export default function Today(){

    const { habits, setHabits } = useContext(HabitsContext)

    const serializedUsedData = localStorage.getItem("localUserData");
    const localUserData = JSON.parse(serializedUsedData);

    const [saveButtonColor, setSaveButtonColor] = useState("#EBEBEB");
    const [validateButton, setValidateButton] = useState(false);
    const [habitsToday, setHabitsToday] = useState([]);

    useEffect(()=> {
        const promisse = axios.get('https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits/today', {

            headers:{"Authorization": `Bearer ${localUserData.token}`}

        });

            promisse.then((response) => {

                setHabitsToday(response.data);

            });
            promisse.catch(() => window.alert("deu ruim..."));

    },[localUserData.token])


    function saveHabit(habitId, done){

        console.log(done)

        const promisse = axios.post(`https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits/${habitId}/check`,{

            headers:{"Authorization": `Bearer ${localUserData.token}`}

        });

        promisse.then(
            () => {
                const promisse = axios.get('https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/habits', {

                    headers:{"Authorization": `Bearer ${localUserData.token}`}

                });

                promisse.then((response) => {

                    setHabitsToday(response.data);

                });
                promisse.catch(() => window.alert("deu ruim..."));
            }
        );

    }

    return(
        <>  
            <Header/>
            <Date>
                <h2>Segunda, 17/05</h2>
                <p>Nenhuma hábito concluído ainda</p>
            </Date>
            {
                habitsToday.map((habit)=>{
                    return(
                        <ConfigureHabit key={habit.id}>
                            <Habit>
                                <HabitName>
                                    {habit.name}
                                </HabitName>
                                <Days >
                                    <p>Sequência atual: {habit.currentSequence}</p>
                                    <p>Seu recorde: {habit.highestSequence}</p>
                                </Days>
                            </Habit>
                            <Check key={habit.id} saveButtonColor={saveButtonColor} id={habit.id} onClick={()=> saveHabit(habit.id, habit.done)}>
                                <img src={check} alt="check"/>
                            </Check>
                        </ConfigureHabit>
                    )

                })
            } 
            <Footer/>
        </>
    );
}

const Date = styled.div`
    height: 10vh;
    width: 100%;

    margin-top: 12vh;
    box-sizing: border-box;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: left;

    font-size: 22px;
    color: #126BA5;

        button{
            height: 40px;
            width: 40px;
            background-color:#52B6FF;

            font-size: 30px;
            font-style: bold;
            color: #FFFFFF;
        }
`;
const ConfigureHabit = styled.div`
    height: 13vh;
    width: 100%;
    background-color: #ffffff;
    color: #666666;

    border-radius: 5px;
    padding: 15px;
    box-sizing: border-box;
    margin-bottom: 10px;

    display: flex;
    flex-direction: row;
    justify-content: space-between;
`;

const Habit = styled.div`
    height: 100%;
    width: 75%;

    display: flex;
    flex-direction: column;
    justify-content: space-between;
`;

const HabitName = styled.h1`
    height: 60%;
    font-size: 20px;
`;

const Days = styled.div`
    font-size: 13px;
`;

const Check = styled.button`
    height:100%;
    width: 70px;
    background-color: ${props => props.check ? "#8FC549" : "#E7E7E7"};
    border-radius: 5px;

    display: flex;
    justify-content: center;
    align-items: center;
`;
